# –æ—Ç–∫—Ä—ã–≤–∞–µ—Ç —Å–µ—Å—Å–∏—é –ø—Ä–∏ –æ–±—Ä–∞—â–µ–Ω–∏–∏ —Å –∞–≤—Ç–æ–∑–∞–∫—Ä—ã—Ç–∏–µ–º
@contextmanager
def get_session():
    session = SessionLocal()
    try:
        yield session
    finally:
        session.close()


class Creator:

    # –§—É–Ω–∫—Ü–∏—è –≤—ã–±–∏—Ä–∞–µ—Ç —Ç–µ–º—É –¥–ª—è –∫–Ω–∏–≥–∏
    @staticmethod
    async def select_theme(session: Session):
        try:
            cats = session.query(Category).all()
            all_themes = await read_data_file('books/all_themes.json')

            # –°–æ–∑–¥–∞–µ–º —Å–ø–∏—Å–æ–∫ –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏–π (id –∫–æ—Ç–æ—Ä—ã—Ö –µ—Å—Ç—å –≤ all_themes)
            available_cat_ids = [int(cat_id) for cat_id in all_themes]
            available_cats = [
                cat for cat in cats if cat.id in available_cat_ids]

            if not available_cats:  # –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –≤–æ–æ–±—â–µ –¥–æ—Å—Ç—É–ø–Ω—ã–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
                return False, False, False

            # –í—ã–±–∏—Ä–∞–µ–º –∫–∞—Ç–µ–≥–æ—Ä–∏—é –∏–∑ –¥–æ—Å—Ç—É–ø–Ω—ã—Ö
            cat = random.choice(available_cats)
            themes = all_themes.get(str(cat.id), {}).get(
                'themes', [[]])[1]  # –ë–µ–∑–æ–ø–∞—Å–Ω–æ–µ –ø–æ–ª—É—á–µ–Ω–∏–µ —Ç–µ–º

            if themes:
                return random.choice(themes), all_themes, cat

            # –õ–æ–≥–∏–∫–∞ –ø–æ–≤—Ç–æ—Ä–Ω–æ–≥–æ –≤—ã–±–æ—Ä–∞, –µ—Å–ª–∏ —Ç–µ–º—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã –¥–ª—è –≤—ã–±—Ä–∞–Ω–Ω–æ–π –¥–æ—Å—Ç—É–ø–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏.  –ù–µ –¥–æ–ª–∂–Ω–æ —Å—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å, –Ω–æ –Ω–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π –æ—Å—Ç–∞–≤–∏–ª.
            else:
                remaining_cats = available_cats.copy()
                remaining_cats.remove(cat)
                while remaining_cats:
                    cat = random.choice(remaining_cats)
                    themes = all_themes.get(
                        str(cat.id), {}).get('themes', [[]])[1]
                    if themes:
                        return random.choice(themes), all_themes, cat
                    remaining_cats.remove(cat)

                return False, False, False  # –ï—Å–ª–∏ —Ç–µ–º—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã –Ω–∏ –¥–ª—è –æ–¥–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏

        finally:
            session.close()

    #
    @staticmethod
    async def create_book_in_bd(
        session: Session, user_id: str, name_book: str, content: dict, book_url: str, access_token: str, category_id: int
    ) -> Book:
        try:
            # –°–æ–∑–¥–∞—ë–º –∫–Ω–∏–≥—É
            book = Book(
                user_id=user_id, name_book=name_book, content=content,
                book_url=book_url, access_token=access_token, category_id=category_id
            )
            session.add(book)
            session.commit()
            session.refresh(book)

            return book

        finally:
            session.close()

    # –§—É–Ω–∫—Ü–∏—è —Å–æ–∑–¥–∞–µ—Ç —Ç–µ–º—ã –¥–ª—è –∫–Ω–∏–≥–∏
    async def auto_themes_creator(session: Session):
        cats = await CategoryCRUD.get_all_categories(session)
        all_themes = {}
        for cat in tqdm(cats, 'Generating themes'):
            themes = await generate_themes_book(cat.name)
            all_themes[cat.id] = {
                "name": cat.name,
                "themes": themes
            }
            await asyncio.sleep(15)

            all_themes_data = json.dumps(
                all_themes, ensure_ascii=False, indent=4)
            await write_data_file(f'books/all_themes.json', all_themes_data)
            return all_themes


# –§—É–Ω–∫—Ü–∏—è –ø–æ–ª–Ω–æ—Å—Ç—å—é —Å–æ–∑–¥–∞–µ—Ç –∫–Ω–∏–≥—É
async def auto_book_creator():
    if not gemini_key:
        print(f'GeminiApiKey is None')
        return False
    try:
        with get_session() as session:
            theme, all_themes, cat = await Creator.select_theme(session)
        if theme and all_themes and cat:
            print('–í—ã–±—Ä–∞–ª–∏ —Ç–µ–º—É')
            await asyncio.sleep(3)

            qt_topics = random.randint(5, 10)
            result, topics = await generate_topics_book(theme, qt_topics, gemini_api_key=gemini_key)
            print('–ü–æ–ª—É—á–∏–ª–∏ –≥–ª–∞–≤—ã')
            if result:
                await asyncio.sleep(5)
                # –≥–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–Ω–∏–≥–∏
                full_book = await generate_book(theme, topics, gemini_api_key=gemini_key)
                print('–ü–æ–ª—É—á–∏–ª–∏ –∫–Ω–∏–≥—É')
                if full_book:
                    # —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –∫–Ω–∏–≥–∏ –≤ —Ñ–∞–π–ª
                    books_data = json.dumps({
                        'book_name': theme,
                        'books_topics': topics,
                        'full_book': full_book
                    }, ensure_ascii=False, indent=4)
                    # —Å–æ–∑–¥–∞–µ–º –∫–Ω–∏–≥—É –≤ —Ç–µ–ª–µ–≥—Ä–∞—Ñ–µ
                    book_url, access_token = await create_book_in_telegraph('Aleks', json.loads(books_data), 'App 5', 'https://t.me/app5_bot')
                    if book_url:
                        print('–ó–∞–≥—Ä—É–∑–∏–ª–∏ –∫–Ω–∏–≥—É –≤ Telegra.ph')
                        # —Å–æ—Ö—Ä–∞–Ω—è–µ–º –∫–Ω–∏–≥—É –≤ —Ñ–∞–π–ª
                        full_books_data = json.dumps({
                            'book_name': theme,
                            'books_topics': topics,
                            'full_book': full_book,
                            'book_url': book_url,
                            'access_token': access_token
                        }, ensure_ascii=False, indent=4)
                        # –∑–∞–Ω–æ—Å–∏–º –∫–Ω–∏–≥—É –≤ –ø–∞–ø–∫—É –∏—Å—Ç–æ—Ä–∏—è –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –≤—Å–µ—Ö –∫–Ω–∏–≥
                        await write_data_file(f'history/{theme}.json', full_books_data)
                        msg_book = f'üìö <b><a href="{book_url}">{theme}</a></b>'

                        with get_session() as session:
                            # –∑–∞–Ω–æ—Å–∏–º –≤ –±–¥
                            book = await Creator.create_book_in_bd(
                                session, '6316305521', theme, books_data, book_url, access_token, cat.id
                            )

                            if book:
                                print('–°–æ—Ö—Ä–∞–Ω–∏–ª–∏ –∫–Ω–∏–≥—É –≤ –±–¥')
                                await send_message_admin(msg_book)

                                # –û–±–Ω–æ–≤–ª—è–µ–º —Ñ–∞–π–ª all_themes.json, —É–¥–∞–ª—è—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–Ω—É—é —Ç–µ–º—É
                                try:
                                    all_themes[str(cat.id)]["themes"][1].remove(
                                        theme)
                                    if not all_themes[str(cat.id)]["themes"][1]:
                                        del all_themes[str(cat.id)]
                                    new_all_themes_data = json.dumps(
                                        all_themes, ensure_ascii=False, indent=4)
                                    await write_data_file('books/all_themes.json', new_all_themes_data)
                                    return True

                                except Exception as e:
                                    print(e)
                                    await send_message_admin(f'–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ —Ñ–∞–π–ª–∞, —Ç–µ–ºa: <b>{theme}</b>')
                            else:
                                await send_message_admin(f'–ù–µ —É–¥–∞–ª–æ—Å—å –∑a–ø–∏—Å–∞—Ç—å –∫–Ω–∏–≥—É –≤ –±–¥:\n\n {msg_book}')
                    else:
                        await send_message_admin(f'–ù–µ —É–¥–∞–ª–æ—Å—å –∑a–ø–∏—Å–∞—Ç—å –∫–Ω–∏–≥—É –≤ —Ç–µ–ª–µ–≥—Ä–∞—Ñ: <b>{theme}</b>')
                else:
                    await send_message_admin(f'–ù–µ —É–¥–∞–ª–æ—Å—å —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å –∫–Ω–∏–≥—É: <b>{theme}</b>')
            else:
                await send_message_admin(f'–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –∫–Ω–∏–≥—É: <b>{theme}</b>')
        else:
            await send_message_admin(f'–ù–µ —É–¥–∞–ª–æ—Å—å –≤–∑—è—Ç—å —Ç–µ–º—ã –¥–ª—è: <b>{cat.name}</b>')

    except Exception as e:
        print(e)


