BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "users" (
	"id"	INTEGER NOT NULL,
	"user_id"	VARCHAR NOT NULL,
	"pro"	BOOLEAN,
	"charge_id"	VARCHAR NOT NULL,
	"balance"	INTEGER,
	"subscribed"	BOOLEAN,
	"date"	DATETIME,
	UNIQUE("charge_id"),
	UNIQUE("user_id"),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "categories" (
	"id"	INTEGER NOT NULL,
	"code"	VARCHAR NOT NULL,
	"name"	VARCHAR NOT NULL,
	"description"	VARCHAR NOT NULL,
	"img"	VARCHAR NOT NULL,
	"translations"	JSON,
	PRIMARY KEY("id"),
	UNIQUE("code")
);
CREATE TABLE IF NOT EXISTS "payments" (
	"id"	INTEGER NOT NULL,
	"user_id"	INTEGER,
	"amount"	INTEGER NOT NULL,
	"charge_id"	VARCHAR NOT NULL,
	"date"	DATETIME,
	"refunded"	BOOLEAN,
	PRIMARY KEY("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id")
);
CREATE TABLE IF NOT EXISTS "books" (
	"id"	INTEGER NOT NULL,
	"user_id"	INTEGER,
	"category_id"	INTEGER,
	"name_book"	VARCHAR NOT NULL,
	"description_book"	VARCHAR NOT NULL,
	"content"	JSON NOT NULL,
	"book_url"	VARCHAR NOT NULL,
	"access_token"	VARCHAR,
	PRIMARY KEY("id"),
	FOREIGN KEY("category_id") REFERENCES "categories"("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id")
);
CREATE TABLE IF NOT EXISTS "orders" (
	"id"	INTEGER NOT NULL,
	"user_id"	INTEGER,
	"book_id"	INTEGER,
	"amount"	INTEGER,
	"book_url"	VARCHAR NOT NULL,
	"date"	DATETIME,
	PRIMARY KEY("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id"),
	FOREIGN KEY("book_id") REFERENCES "books"("id")
);
